const initialState = {
    fructs: [],
    loading: false,
    error: false,
    cartElements: [],
    orderTotal: 35
};

const reducers = (state = initialState, action) => {

    
    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        case 'FRUCT_ADDED_TO_CART': 

            const idx = state.fructs.find((id) => id === action.payload );

            console.log(idx);

            return {
                ...state,
                cartElements: []
            }
        default: 
            return state;
    };
};

export default reducers;
