const initialState = {
    fructs: [],
    loading: false,
    error: false,
    cartElements: [],
    orderTotal: 35
};

const updateCartElements = (cartItems, { newItem, itemIndex }) => {

    if(newItem.count == 0) {
        return [
            ...cartItems.slice(0, itemIndex),
            ...cartItems.slice(itemIndex + 1 )
        ];
    }
    
    if (itemIndex == -1) {
        return [
            ...cartItems,
            newItem
        ];
    };

    return [
        ...cartItems.slice(0, itemIndex),
        newItem,
        ...cartItems.slice(itemIndex + 1 )
    ];
};

const updateCartElement = (bookId, state, action) => {

    const { bookList: { books }, shoppingCart: { cartItems, } } = state;

    const book = books.find((book) => book.id === bookId);
    const itemIndex = cartItems.findIndex(({id}) => id === bookId);
    const item = cartItems[itemIndex];

    const itemDefault = (book, item = {}) => {
        const {
            id = book.id,
            title = book.title,
            count = 0,
            total = 0 } = item;

        return {
            id,
            title,
            count,
            total
        };
    }

    const {
        id,
        title,
        count,
        total} = itemDefault(book, item);

    if(action == 'DECREASE_BOOK_COUNT') {
        return {
            newItem: {
                id: book.id,
                title: book.title,
                count: count - 1,
                total: total - book.price
            },
            itemIndex
        }
        
    }else if(action == 'BOOK_ADDED_TO_CART' || 'INCREASE_BOOK_COUNT') {
        return {
            newItem: {
                id,
                title,
                count: count + 1,
                total: total + book.price    
            },
            itemIndex
        }
    };
};

const reducers = (state = initialState, action) => {


    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        case 'FRUCT_ADDED_TO_CART': 
            return {
                ... state,
                cartElements: updateCartElements( cartElements, updateCartElement(action.payload, state, action.type) )
            }
        default: 
            return state;
    };
};

export default reducers;
