const initialState = {
    fructs: [],
    loading: false,
    error: false,
    cartElements: [],
    orderTotal: 35
};

const updateCartElements = (cartElements, { newElement, elementIndex }) => {

    if(newElement.count == 0) {
        return [
            ...cartElements.slice(0, elementIndex),
            ...cartElements.slice(elementIndex + 1 )
        ];
    }
    
    if (elementIndex == -1) {
        return [
            ...cartElements,
            newElement
        ];
    };

    return [
        ...cartElements.slice(0, elementIndex),
        newElement,
        ...cartElements.slice(elementIndex + 1 )
    ];
};

const updateCartElement = (elementId, state, action) => {

    const { fructs, cartElements} = state;

    const fruct = fructs.find((fruct) => fruct.id === elementId);
    const elementIndex = cartElements.findIndex(({id}) => id === elementId);
    const element = cartElements[elementIndex];

    const ElementDefault = (fruct, element = {}) => {
        const {
            id = fruct.id,
            title = fruct.title,
            count = 0,
            total = 0 } = fruct;

        return {
            id,
            title,
            count,
            total
        };
    }

    const {
        id,
        title,
        count,
        total} = ElementDefault(fruct, element);

    if(action == 'DECREASE_FRUCT_COUNT') {
        return {
            newElement: {
                id: fruct.id,
                title: fruct.title,
                count: count - 1,
                total: total - fruct.price
            },
            elementIndex
        }
        
    }else if(action == 'FRUCT_ADDED_TO_CART' || 'INCREASE_FRUCT_COUNT') {
        return {
            newElement: {
                id,
                title,
                count: count + 1,
                total: total + fruct.price    
            },
            elementIndex
        }
    };
};

const reducers = (state = initialState, action) => {


    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        case 'FRUCT_ADDED_TO_CART': 

        console.log(state.cartElements)
            return {
                ...state,
                cartElements: updateCartElements( state.cartElements, updateCartElement(action.payload, state, action.type) )
            }
        default: 
            return state;
    };
};

export default reducers;
