const initialState = {
    fructs: [],
    loading: false,
    error: false,
    cartElements: [],
    orderTotal: 35
};

const updateCartElements = (cartElements, { newElement, elementIndex }) => {

    if(newElement.count == 0) {
        return [
            ...cartElements.slice(0, elementIndex),
            ...cartElements.slice(elementIndex + 1 )
        ];
    }
    
    if (elementIndex == -1) {
        return [
            ...cartElements,
            newElement
        ];
    };

    return [
        ...cartElements.slice(0, elementIndex),
        newElement,
        ...cartElements.slice(elementIndex + 1 )
    ];
};

