const updateCartElement = (elementId, state, action) => {

    const { fructs, cartElements} = state;

    const fruct = fructs.find((fruct) => fruct.id === elementId);
    const elementIndex = cartElements.findIndex(({id}) => id === elementId);
    const element = cartElements[elementIndex];
    const elementdiscount = cartElements.find(({discount}) => discount === true );


    if(cartElements.length === 0) {

        const { 
            id,
            title,
            discount,
            count,
            total
        } = ElementDefault(fruct, element, { discount: null, count: 0, total: 0});

        if(action == 'FRUCT_ADDED_TO_CART') {
            return {
                newElement: {
                    id,
                    title,
                    discount,
                    count: count + 1,
                    total: total + 8   
                },
                elementIndex
            }
        }
    }else if(elementdiscount.discount === true && elementdiscount.count === 3) {
        const { 
            id,
            title,
            discount,
            count,
            total
        } = ElementDefault(fruct, element, { discount: true, count: 3, total: 25});

        if(action == 'DECREASE_FRUCT_COUNT') {
            return {
                newElement: {
                    id,
                    title,
                    discount,
                    count: count - 1,
                    total: total - 8
                },
                elementIndex
            }
            
        }
    }else {
        const { 
            id,
            title,
            discount,
            count,
            total
        } = ElementDefault(fruct, element, { discount: false, count: 3, total: 25});

        if(action == 'DECREASE_FRUCT_COUNT') {
            return {
                newElement: {
                    id: fruct.id,
                    title: fruct.title,
                    discount,
                    count: count - 1,
                    total: total - fruct.price
                },
                elementIndex
            }
            
        }else if(action == 'FRUCT_ADDED_TO_CART' || 'INCREASE_FRUCT_COUNT') {
            return {
                newElement: {
                    id,
                    title,
                    discount,
                    count: count + 1,
                    total: total + fruct.price    
                },
                elementIndex
            }
        }
    }
};

const updateOrderTotal = (cartElements) => {
    const result = cartElements.map(({total}) => total);
    console.log(result);
};

const reducers = (state = initialState, action) => {


    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        case 'FRUCT_ADDED_TO_CART': 

            console.log(state.cartElements)

            return {
                ...state,
                cartElements: updateCartElements( state.cartElements, updateCartElement(action.payload, state, action.type) ),
                orderTotal: updateOrderTotal(state.cartElements)
            }
        case 'DECREASE_FRUCT_COUNT': 

            console.log(state.cartElements)

            return {
                ...state,
                cartElements: updateCartElements( state.cartElements, updateCartElement(action.payload, state, action.type) )
            }
        case 'INCREASE_FRUCT_COUNT': 

            console.log(state.cartElements)

            return {
                ...state,
                cartElements: updateCartElements( state.cartElements, updateCartElement(action.payload, state, action.type) )
            }
        case 'DELETE_FRUCT' :

            const { cartElements } = state;

            const id  = cartElements.findIndex(({ id }) => id === action.payload );

            return {
                ... state,
                cartElements: [
                    ...cartElements.slice(0, id),
                    ...cartElements.slice(id + 1)
                ]
            };

        default: 
            return state;
    };
};

export default reducers;
