const initialState = {
    fructs: [],
    loading: false,
    error: false,
    cartElements: [],
    orderTotal: 35
};

const addCartElement = (fructs, cartElements, elementIndex) => {

    if(cartElements.length == 0) {

    } 
    else {
        cartElements.find(({id}) => id == elementIndex);
    }

    const idx = fructs.find(({id}) => action.payload === id );

    console.log(idx)
    
}

const reducers = (state = initialState, action) => {


    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        case 'FRUCT_ADDED_TO_CART': 

            // console.log(cartElements);

            return {
                ...state,
                cartElements: addCartElement(fructs, cartElements, action.payload)
            }
            
        default: 
            return state;
    };
};

export default reducers;
