const getProducts = (state, action) => {

    if(state === undefined) {
        return {
            fructs: [],
            loading: false,
            error: null,
        }
    }
    
    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        default: 
            return state.getProducts;
    }
};

export default getProducts;
