const initialState = {
    fructs: [],
    loading: false,
    error: false,
    cartElements: [],
    orderTotal: 35
};

const addCartElement = (fructs, cartElements, elementIndex) => {
    return [
        ...cartElements.slice(0, elementIndex.id),
        ...cartElements.slice(elementIndex.id, + 1)
    ]
}

const reducers = (state = initialState, action) => {


    switch(action.type) {
        case 'FETCH_FRUCTS_REQUREST': 
            return {
                ...state,
                fructs: [],
                loading: true,
                error: null
            };
        case 'FETCH_FRUCTS_SUCCESS': 
            return {
                ...state,
                fructs: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_FRUCTS_FAILURE': 
            return {
                ...state,
                fructs: [],
                loading: false,
                error: action.payload
            };
        case 'FRUCT_ADDED_TO_CART': 

            const { fructs, cartElements } = state;

            const idx = fructs.find(({id}) => action.payload === id );

            return {
                ...state,
                cartElements: addCartElement(fructs, cartElements, idx)
            }
            
        default: 
            return state;
    };
};

export default reducers;
